mapper.select:
SELECT * FROM deal, portfolio
WHERE deal_portfolio_id = portfolio_id
AND deal_purUser = #{user_id};

mapper.update:
UPDATE deal SET
deal_status = '구매 확정'
WHERE deal_id = #{deal_id};

mapper.select:
SELECT * FROM inquiry, deal, portfolio
WHERE inq_deal_id = deal_id
AND deal_portfolio_id = portfolio_id
AND inq_purUser = #{user_id}
ORDER BY inq_id DESC;

mapper.insert:
INSERT INTO inquiry(
	inq_deal_id,
	inq_purUser,
	inq_title,
	inq_content
) VALUES (
	#{inq_deal_id},
	#{inq_purUser},
	#{inq_title},
	#{inq_content}
);

mapper.select:
SELECT * FROM inquiry, deal, portfolio
WHERE inq_deal_id = deal_id
AND deal_portfolio_id = portfolio_id
AND deal_saleUser = #{user_id}
ORDER BY portfolio_id;

mapper.insert:
INSERT INTO answer(
	ans_deal_id,
	ans_inq_id,
	ans_saleUser,
	ans_content
) VALUES (
	#{ans_deal_id},
	#{ans_inq_id},
	#{ans_saleUser},
	#{ans_content}
);

mapper.update:
UPDATE user SET
user_name = #{user_name},
user_phone = #{user_phone},
user_email = #{user_email}
WHERE user_id = #{user_id}

UPDATE user SET
user_pw = #{user_pw}
WHERE user_id = #{user_id}
